mapper.update:
update userinfo set userPw=#{userPw}
				,userEmail=#{userEmail}
				,userPhone=#{userPhone}
				,userName=#{userName}
where userId=#{userId}

mapper.select:

select userName, userId, userEmail, role
from userinfo
where userId=#{userId} and userPw=#{userPw}

select userName, userId, userEmail, joinDate, role
from userinfo
where userId = #{userId}
